import React, { useState, useRef, useEffect } from 'react';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [selectedModel, setSelectedModel] = useState('chat');
  const [selectedFile, setSelectedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [sessionId] = useState(() => `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);

  const fileInputRef = useRef(null);
  const messagesEndRef = useRef(null);

  const models = [
    { value: 'chat', label: 'LLaMA 3.1 8B', endpoint: '/model/chat' },
    { value: 'qwen', label: 'Qwen 2.5 VL 7B', endpoint: '/model/qwen' },
    { value: 'gemma', label: 'Gemma 3 12B', endpoint: '/model/gemma' },
    { value: 'image', label: 'Stable Diffusion XL', endpoint: '/model/generate-image' }
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
    }
  };

  const removeFile = () => {
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputText.trim() && !selectedFile) return;

    const userMessage = inputText.trim();
    setInputText('');
    setIsLoading(true);

    // Add user message to chat
    const newUserMessage = {
      id: Date.now(),
      role: 'user',
      text: userMessage,
      timestamp: Date.now(),
      file: selectedFile
    };

    setMessages(prev => [...prev, newUserMessage]);

    try {
      let response;
      const selectedModelConfig = models.find(m => m.value === selectedModel);

      if (selectedModel === 'image') {
        // Handle image generation
        response = await fetch(`http://localhost:4001${selectedModelConfig.endpoint}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            prompt: userMessage
          })
        });

        if (response.ok) {
          const imageBlob = await response.blob();
          const imageUrl = URL.createObjectURL(imageBlob);

          const assistantMessage = {
            id: Date.now() + 1,
            role: 'assistant',
            text: '',
            timestamp: Date.now(),
            imageUrl: imageUrl
          };

          setMessages(prev => [...prev, assistantMessage]);
        }
      } else {
        // Handle text chat models
        let requestBody = {
          sessionId: sessionId
        };

        if (selectedModel === 'chat') {
          requestBody.userMessage = userMessage;
        } else if (selectedModel === 'qwen') {
          requestBody.userMessage = userMessage;
          if (selectedFile) {
            // For demo purposes, we'll just pass the filename
            // In a real app, you'd upload the file and get a URL
            requestBody.imageUrl = selectedFile.name;
          }
        } else if (selectedModel === 'gemma') {
          requestBody.prompt = userMessage;
          if (selectedFile) {
            requestBody.imageurl = selectedFile.name;
          }
        }

        response = await fetch(`http://localhost:4001${selectedModelConfig.endpoint}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(requestBody)
        });

        if (response.ok) {
          const data = await response.json();

          const assistantMessage = {
            id: Date.now() + 1,
            role: 'assistant',
            text: data.reply,
            timestamp: Date.now()
          };

          setMessages(prev => [...prev, assistantMessage]);
        }
      }
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = {
        id: Date.now() + 1,
        role: 'assistant',
        text: 'Sorry, I encountered an error processing your request.',
        timestamp: Date.now(),
        isError: true
      };

      setMessages(prev => [...prev, errorMessage]);
    }

    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    setIsLoading(false);
  };

  const startNewChat = () => {
    setMessages([]);
    setInputText('');
    setSelectedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="app">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <button className="new-chat-btn" onClick={startNewChat}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 5V19M5 12H19" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
            </svg>
            New chat
          </button>
        </div>

        <div className="sidebar-content">
          <div className="chat-history">
            <div className="section-title">Today</div>
            <div className="chat-item active">Current Chat</div>
          </div>
        </div>

        <div className="sidebar-footer">
          <div className="user-info">
            <div className="user-avatar">U</div>
            <span>User</span>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="chat-header">
          <div className="model-selector">
            <select 
              value={selectedModel} 
              onChange={(e) => setSelectedModel(e.target.value)}
              className="model-dropdown"
            >
              {models.map(model => (
                <option key={model.value} value={model.value}>
                  {model.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="messages-container">
          {messages.length === 0 ? (
            <div className="welcome-screen">
              <h1>What's on your mind today?</h1>
            </div>
          ) : (
            <div className="messages">
              {messages.map((message) => (
                <div key={message.id} className={`message ${message.role}`}>
                  <div className="message-avatar">
                    {message.role === 'user' ? 'U' : 'AI'}
                  </div>
                  <div className="message-content">
                    {message.file && (
                      <div className="message-file">
                        📎 {message.file.name}
                      </div>
                    )}
                    {message.text && (
                      <div className={`message-text ${message.isError ? 'error' : ''}`}>
                        {message.text}
                      </div>
                    )}
                    {message.imageUrl && (
                      <div className="message-image">
                        <img src={message.imageUrl} alt="Generated" />
                      </div>
                    )}
                  </div>
                </div>
              ))}
              {isLoading && (
                <div className="message assistant">
                  <div className="message-avatar">AI</div>
                  <div className="message-content">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>
          )}
        </div>

        <div className="input-container">
          {selectedFile && (
            <div className="selected-file">
              <span>📎 {selectedFile.name}</span>
              <button onClick={removeFile} className="remove-file">×</button>
            </div>
          )}

          <form onSubmit={handleSubmit} className="input-form">
            <div className="input-wrapper">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileSelect}
                accept="image/*"
                style={{ display: 'none' }}
              />

              <button
                type="button"
                className="attach-btn"
                onClick={() => fileInputRef.current?.click()}
                title="Attach image"
              >
                📎
              </button>

              <input
                type="text"
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                placeholder={selectedModel === 'image' ? 'Describe the image you want to generate...' : 'Ask anything'}
                className="message-input"
                disabled={isLoading}
              />

              <button
                type="submit"
                className="send-btn"
                disabled={(!inputText.trim() && !selectedFile) || isLoading}
              >
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M7 11L12 6L17 11M12 18V7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default App;
